import './Expenses.css';
import ExpenseItem from "./ExpenseItem";
import ExpenseFilter from '../Expenses/ExpenseFilter';
import Card from '../UI/Card';
import React, { useState } from 'react';



function Expenses (props) {

    const [selectedYear, setSelectedYear] = useState('2020');

    const filterChangeHandler = selectedYear => {
        setSelectedYear(selectedYear);
    }

    const filteredExpenses = props.expenses.filter(expense => expense.date.getFullYear().toString() === selectedYear);

    return (
        <div>
        <ExpenseFilter selectedYear = {selectedYear} onChangeFilter = {filterChangeHandler}/>
        <Card className = "expenses">
            {filteredExpenses.length === 0 ? <p>No expenses. If you add an expense it will show here! </p> : filteredExpenses
                            .map(e => <ExpenseItem key={e.id}
                                                   title={e.title}
                                                   amount={e.amount}
                                                   date={e.date} />) }
            
          </Card>
        </div>
    )
}

export default Expenses;
